mapper.select:
select * from dm_wf_nodezb where 1=1

  and pre_wfnodezb_dm = #{preWfnodezbDm,jdbcType=INTEGER}


  and wfmc = #{wfmc,jdbcType=VARCHAR}


  and wf_desc = #{wfDesc,jdbcType=VARCHAR}


  and rygwdz_dm = #{rygwdzDm,jdbcType=INTEGER}


  and lczt_dm = #{lcztDm,jdbcType=INTEGER}


  and dept_dm = #{deptDm,jdbcType=INTEGER}


  and lcjdbj = #{lcjdbj,jdbcType=INTEGER}


  and zsbj = #{zsbj,jdbcType=INTEGER}


  and qybj = #{qybj,jdbcType=INTEGER}


  and czrq = #{czrq,jdbcType=TIMESTAMP}


  and xgrq = #{xgrq,jdbcType=TIMESTAMP}


  and user_dm = #{userDm,jdbcType=INTEGER}
 limit 1

select n.next_wfnodezb_dm from dm_cs_user u
INNER JOIN dm_wf_nodezb n
where u.dept_dm = n.dept_dm and n.qybj = 1 and u.qybj = 1 and u.user_dm = #{userDm}

select n.wfnodezb_dm from dm_cs_user u
INNER JOIN dm_wf_nodezb n
where u.dept_dm = n.dept_dm and n.qybj = 1 and u.qybj = 1 and u.user_dm = #{userId}
